import React, { useState, useEffect } from "react";

export default function TodoCard({ todo, onEdit, onDelete, onChangeStatus, index = 0 }) {
  const [openStatus, setOpenStatus] = useState(false);
  const delay = Math.min(index * 60, 300);

  useEffect(() => {
    // small staggered entrance
    const el = document.querySelector(`[data-id="${todo.id}"]`);
    if (el) {
      el.style.setProperty("--enter-delay", `${delay}ms`);
    }
  }, [todo.id, delay]);

  function toggleStatusDropdown(e) {
    e.stopPropagation();
    setOpenStatus(v => !v);
  }

  function handleSelect(status) {
    onChangeStatus(status);
    setOpenStatus(false);
  }

  return (
    <div
      className={`card todo-card animated-card ${todo.status === "completed" ? "done" : ""}`}
      data-id={todo.id}
    >
      <div className="card-top">
        <div className="title">
          <strong>{todo.name}</strong>
          <div className="meta">
            <small>{new Date(todo.createdAt).toLocaleString()}</small>
          </div>
        </div>

        <div className="actions">
          <div className="status-control">
            <button
              className={`btn status-btn ${todo.status === "completed" ? "complete" : "not"}`}
              onClick={toggleStatusDropdown}
              aria-haspopup="true"
              aria-expanded={openStatus}
            >
              <span className="status-dot" aria-hidden />
              {todo.status === "completed" ? "Completed" : "Not Completed"}
            </button>

            {openStatus && (
              <div className="status-dropdown" onClick={(e) => e.stopPropagation()}>
                <div className="option" onClick={() => handleSelect("completed")}>Completed</div>
                <div className="option" onClick={() => handleSelect("notcompleted")}>Not Completed</div>
              </div>
            )}
          </div>

          <button className="btn small" onClick={onEdit}>Edit</button>
          <button className="btn small danger" onClick={onDelete}>Delete</button>
        </div>
      </div>

      <div className="card-body">
        <p>{todo.desc || <i>No description</i>}</p>
      </div>
    </div>
  );
}
