import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [taskName, setTaskName] = useState("");
  const [editIndex, setEditIndex] = useState(null);
  const [filterStatus, setFilterStatus] = useState("all");

  const handleAddOrUpdate = () => {
    if (!taskName.trim()) return;

    const newTask = { name: taskName, status: false, id: Date.now() };

    if (editIndex !== null) {
      const updatedTodos = [...todos];
      updatedTodos[editIndex].name = taskName;
      setTodos(updatedTodos);
      setEditIndex(null);
    } else {
      setTodos([...todos, newTask]);
    }

    setTaskName("");
  };

  const handleDelete = (id) => {
    setTodos((prev) => prev.map(todo =>
      todo.id === id ? { ...todo, deleting: true } : todo
    ));

    // Remove after animation
    setTimeout(() => {
      setTodos((prev) => prev.filter(todo => todo.id !== id));
    }, 300);
  };

  const handleEdit = (index) => {
    setTaskName(todos[index].name);
    setEditIndex(index);
  };

  const toggleStatus = (index) => {
    const updatedTodos = [...todos];
    updatedTodos[index].status = !updatedTodos[index].status;
    setTodos(updatedTodos);
  };

  const filteredTodos = todos.filter(todo => {
    if (filterStatus === "all") return true;
    return filterStatus === "completed" ? todo.status : !todo.status;
  });

  return (
    <div className="app">
      <h1>Google Todo List</h1>

      <div className="input-section">
        <input
          type="text"
          placeholder="Add a task..."
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
        />
        <button onClick={handleAddOrUpdate}>
          {editIndex !== null ? "Update" : "Add"}
        </button>
      </div>

      <div className="filter-section">
        <select onChange={e => setFilterStatus(e.target.value)} value={filterStatus}>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="notcompleted">Not Completed</option>
        </select>
      </div>

      <div className="todos-section">
        {filteredTodos.map((todo, index) => (
          <div
            key={todo.id}
            className={`todo-card ${todo.deleting ? "fade-out" : "slide-in"}`}
          >
            <div className="todo-left">
              <input
                type="checkbox"
                checked={todo.status}
                onChange={() => toggleStatus(index)}
              />
              <span className={todo.status ? "completed" : ""}>{todo.name}</span>
            </div>
            <div className="todo-right">
              <button onClick={() => handleEdit(index)}>Edit</button>
              <button onClick={() => handleDelete(todo.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
