import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [taskName, setTaskName] = useState("");
  const [taskDesc, setTaskDesc] = useState("");
  const [editIndex, setEditIndex] = useState(null);
  const [filterStatus, setFilterStatus] = useState("all");

  // Add or update task
  const handleAddOrUpdate = () => {
    if (!taskName) return;

    const newTask = {
      name: taskName,
      desc: taskDesc,
      status: "Not Completed",
    };

    if (editIndex !== null) {
      const updatedTodos = [...todos];
      updatedTodos[editIndex] = { ...updatedTodos[editIndex], name: taskName, desc: taskDesc };
      setTodos(updatedTodos);
      setEditIndex(null);
    } else {
      setTodos([...todos, newTask]);
    }

    setTaskName("");
    setTaskDesc("");
  };

  // Delete task
  const handleDelete = (index) => {
    const updatedTodos = todos.filter((_, i) => i !== index);
    setTodos(updatedTodos);
  };

  // Edit task
  const handleEdit = (index) => {
    setTaskName(todos[index].name);
    setTaskDesc(todos[index].desc);
    setEditIndex(index);
  };

  // Change status
  const handleStatusChange = (index, newStatus) => {
    const updatedTodos = [...todos];
    updatedTodos[index].status = newStatus;
    setTodos(updatedTodos);
  };

  // Filter tasks
  const filteredTodos = todos.filter((todo) => {
    if (filterStatus === "all") return true;
    return todo.status === filterStatus;
  });

  return (
    <div className="app">
      <h1>Todo App</h1>

      <div className="input-section">
        <input
          type="text"
          placeholder="Task Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={taskDesc}
          onChange={(e) => setTaskDesc(e.target.value)}
        />
        <button onClick={handleAddOrUpdate}>{editIndex !== null ? "Update" : "Add"} Task</button>
      </div>

      <div className="filter-section">
        <label>Filter: </label>
        <select onChange={(e) => setFilterStatus(e.target.value)} value={filterStatus}>
          <option value="all">All</option>
          <option value="Completed">Completed</option>
          <option value="Not Completed">Not Completed</option>
        </select>
      </div>

      <div className="todos-section">
        {filteredTodos.map((todo, index) => (
          <div className="todo-card" key={index}>
            <h3>{todo.name}</h3>
            <p>{todo.desc}</p>
            <div className="status-edit">
              <select
                value={todo.status}
                onChange={(e) => handleStatusChange(index, e.target.value)}
              >
                <option value="Not Completed">Not Completed</option>
                <option value="Completed">Completed</option>
              </select>
              <button onClick={() => handleEdit(index)}>Edit</button>
              <button onClick={() => handleDelete(index)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
