import React from "react";

export default function Filter({ value, onChange, counts }) {
  return (
    <div className="card filter-card">
      <h4>Filter</h4>
      <div className="filters">
        <button
          className={`btn ${value === "all" ? "active" : ""}`}
          onClick={() => onChange("all")}
        >
          All ({counts?.all ?? 0})
        </button>
        <button
          className={`btn ${value === "completed" ? "active" : ""}`}
          onClick={() => onChange("completed")}
        >
          Completed ({counts?.completed ?? 0})
        </button>
        <button
          className={`btn ${value === "notcompleted" ? "active" : ""}`}
          onClick={() => onChange("notcompleted")}
        >
          Not Completed ({counts?.notcompleted ?? 0})
        </button>
      </div>
    </div>
  );
}
