import React, { useState, useEffect } from "react";

export default function App() {
  const [todos, setTodos] = useState(() => {
    const saved = localStorage.getItem("todos");
    return saved ? JSON.parse(saved) : [];
  });

  const [taskName, setTaskName] = useState("");
  const [taskDesc, setTaskDesc] = useState("");
  const [filter, setFilter] = useState("all");
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (!taskName.trim()) return;

    if (editId) {
      setTodos((prev) =>
        prev.map((todo) =>
          todo.id === editId
            ? { ...todo, name: taskName, description: taskDesc }
            : todo
        )
      );
      setEditId(null);
    } else {
      setTodos([
        ...todos,
        {
          id: Date.now(),
          name: taskName,
          description: taskDesc,
          status: "not_completed",
        },
      ]);
    }

    setTaskName("");
    setTaskDesc("");
  };

  const handleDelete = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const handleEdit = (todo) => {
    setTaskName(todo.name);
    setTaskDesc(todo.description);
    setEditId(todo.id);
  };

  const handleStatusChange = (id, status) => {
    setTodos((prev) =>
      prev.map((todo) =>
        todo.id === id ? { ...todo, status: status } : todo
      )
    );
  };

  const filteredTodos = todos.filter((todo) => {
    if (filter === "completed") return todo.status === "completed";
    if (filter === "not_completed") return todo.status === "not_completed";
    return true;
  });

  return (
    <div className="container py-4">
      <h1 className="text-center text-primary mb-4">My Todo</h1>

      {/* Input Form */}
      <form
        onSubmit={handleAddOrUpdate}
        className="row g-2 align-items-center mb-4"
      >
        <div className="col-md-4">
          <input
            type="text"
            placeholder="Todo Name"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
            className="form-control"
          />
        </div>
        <div className="col-md-5">
          <input
            type="text"
            placeholder="Todo Description"
            value={taskDesc}
            onChange={(e) => setTaskDesc(e.target.value)}
            className="form-control"
          />
        </div>
        <div className="col-md-3 d-grid">
          <button type="submit" className="btn btn-success">
            {editId ? "Update Todo" : "Add Todo"}
          </button>
        </div>
      </form>

      {/* Filter */}
      <div className="d-flex justify-content-end mb-3">
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="form-select w-auto"
        >
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="not_completed">Not Completed</option>
        </select>
      </div>

      {/* Todo Cards */}
      <div className="row g-3">
        {filteredTodos.length === 0 ? (
          <p className="text-center">No todos found</p>
        ) : (
          filteredTodos.map((todo) => (
            <div key={todo.id} className="col-md-6 col-lg-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">{todo.name}</h5>
                  <p className="card-text text-muted">
                    {todo.description || "No description"}
                  </p>

                  <div className="mb-2">
                    <label className="form-label">Status</label>
                    <select
                      value={todo.status}
                      onChange={(e) =>
                        handleStatusChange(todo.id, e.target.value)
                      }
                      className="form-select"
                    >
                      <option value="not_completed">Not Completed</option>
                      <option value="completed">Completed</option>
                    </select>
                  </div>

                  <div className="d-flex gap-2">
                    <button
                      className="btn btn-primary w-50"
                      onClick={() => handleEdit(todo)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-danger w-50"
                      onClick={() => handleDelete(todo.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
