import React, { useState } from "react";

export default function TodoCard({ todo, onEdit, onDelete, onChangeStatus }) {
  const [openStatus, setOpenStatus] = useState(false);

  function toggleStatusDropdown(e) {
    e.stopPropagation();
    setOpenStatus(v => !v);
  }

  function handleSelect(status) {
    onChangeStatus(status);
    setOpenStatus(false);
  }

  return (
    <div className={`card todo-card ${todo.status === "completed" ? "done" : ""}`}>
      <div className="card-top">
        <div className="title">
          <strong>{todo.name}</strong>
          <div className="meta">
            <small>Created {new Date(todo.createdAt).toLocaleString()}</small>
          </div>
        </div>
        <div className="actions">
          <div className="status-control">
            <button className="btn status-btn" onClick={toggleStatusDropdown}>
              {todo.status === "completed" ? "Completed ✓" : "Not Completed"}
            </button>
            {openStatus && (
              <div className="status-dropdown" onClick={(e) => e.stopPropagation()}>
                <div className="option" onClick={() => handleSelect("completed")}>Completed</div>
                <div className="option" onClick={() => handleSelect("notcompleted")}>Not Completed</div>
              </div>
            )}
          </div>

          <button className="btn small" onClick={onEdit}>Edit</button>
          <button className="btn small danger" onClick={onDelete}>Delete</button>
        </div>
      </div>

      <div className="card-body">
        <p>{todo.desc || <i style={{ color: "#777" }}>No description</i>}</p>
      </div>
    </div>
  );
}
