import React, { useEffect, useState } from "react";

/**
 * Colorful Animated Todo App
 * - CRUD (Create, Read, Update, Delete)
 * - status dropdown (completed / not_completed)
 * - filter (all / completed / not_completed)
 * - localStorage persistence
 * - animated, colorful UI with CSS (App.css)
 */

export default function App() {
  const [todos, setTodos] = useState(() => {
    try {
      const raw = localStorage.getItem("todos_v2");
      return raw ? JSON.parse(raw) : [];
    } catch {
      return [];
    }
  });

  const [taskName, setTaskName] = useState("");
  const [taskDesc, setTaskDesc] = useState("");
  const [filter, setFilter] = useState("all");
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    localStorage.setItem("todos_v2", JSON.stringify(todos));
  }, [todos]);

  const resetForm = () => {
    setTaskName("");
    setTaskDesc("");
    setEditId(null);
  };

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (!taskName.trim()) return;

    if (editId) {
      setTodos((prev) =>
        prev.map((t) =>
          t.id === editId ? { ...t, name: taskName, description: taskDesc } : t
        )
      );
      resetForm();
    } else {
      const newTodo = {
        id: Date.now(),
        name: taskName.trim(),
        description: taskDesc.trim(),
        status: "not_completed",
        createdAt: new Date().toISOString(),
      };
      setTodos((prev) => [newTodo, ...prev]);
      resetForm();
    }
  };

  const handleDelete = (id) => {
    if (!window.confirm("Delete this task?")) return;
    setTodos((prev) => prev.filter((t) => t.id !== id));
  };

  const handleEdit = (todo) => {
    setTaskName(todo.name);
    setTaskDesc(todo.description);
    setEditId(todo.id);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleStatusChange = (id, status) => {
    setTodos((prev) => prev.map((t) => (t.id === id ? { ...t, status } : t)));
  };

  const filteredTodos = todos.filter((t) => {
    if (filter === "completed") return t.status === "completed";
    if (filter === "not_completed") return t.status === "not_completed";
    return true;
  });

  return (
    <div className="page-bg min-vh-100 py-5">
      <div className="container">
        <header className="text-center mb-4">
          <h1 className="display-5 gradient-text animate-pop">‚ú® Colorful Todo</h1>
          <p className="lead text-muted">Create ‚Äî Update ‚Äî Complete ‚Äî Celebrate üéâ</p>
        </header>

        {/* Form Card */}
        <div className="card glass p-3 mb-4 shadow-lg blur-in">
          <form className="row g-2 align-items-center" onSubmit={handleAddOrUpdate}>
            <div className="col-md-4">
              <input
                className="form-control form-control-lg input-glow"
                placeholder="Task Name (required)"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
                autoFocus
              />
            </div>

            <div className="col-md-5">
              <input
                className="form-control form-control-lg input-glow"
                placeholder="Task Description (optional)"
                value={taskDesc}
                onChange={(e) => setTaskDesc(e.target.value)}
              />
            </div>

            <div className="col-md-3 d-grid gap-2">
              <button type="submit" className="btn btn-hero btn-lg">
                {editId ? "Update Task" : "Add Task"}
              </button>
              {editId && (
                <button
                  type="button"
                  className="btn btn-outline-light"
                  onClick={resetForm}
                >
                  Cancel Edit
                </button>
              )}
            </div>
          </form>
        </div>

        {/* Filter + summary */}
        <div className="d-flex align-items-center justify-content-between mb-3">
          <div className="d-flex align-items-center gap-3">
            <div className="pill stat">
              <strong>{todos.length}</strong>
              <div className="muted">Total</div>
            </div>
            <div className="pill stat">
              <strong>{todos.filter((t) => t.status === "completed").length}</strong>
              <div className="muted">Done</div>
            </div>
            <div className="pill stat">
              <strong>{todos.filter((t) => t.status === "not_completed").length}</strong>
              <div className="muted">Pending</div>
            </div>
          </div>

          <div className="d-flex align-items-center gap-2">
            <select
              className="form-select w-auto"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
            >
              <option value="all">All</option>
              <option value="completed">Completed</option>
              <option value="not_completed">Not Completed</option>
            </select>
            <button
              className="btn btn-sm btn-outline-light"
              onClick={() => {
                if (!todos.length) return alert("No tasks to clear.");
                if (!window.confirm("Clear all tasks?")) return;
                setTodos([]);
              }}
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Cards grid */}
        <div className="row g-3">
          {filteredTodos.length === 0 && (
            <div className="col-12 text-center text-muted py-5">
              <div className="empty-anim">No tasks found ‚Äî add your first colorful task ‚ú®</div>
            </div>
          )}

          {filteredTodos.map((todo) => (
            <div key={todo.id} className="col-md-6 col-lg-4">
              <article className="card todo-card shadow-sm h-100 tilt-in">
                <div className="card-body d-flex flex-column">
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h5 className="card-title mb-0">{todo.name}</h5>
                    <small className="text-muted">{new Date(todo.createdAt).toLocaleString()}</small>
                  </div>

                  <p className="card-text text-secondary mb-3">{todo.description || <em>No description</em>}</p>

                  <div className="mb-3">
                    <div className="d-flex align-items-center gap-2">
                      <span className={`status-badge ${todo.status === "completed" ? "done" : "pending"}`}>
                        {todo.status === "completed" ? "Completed" : "Not Completed"}
                      </span>

                      <select
                        className="form-select form-select-sm w-auto"
                        value={todo.status}
                        onChange={(e) => handleStatusChange(todo.id, e.target.value)}
                      >
                        <option value="not_completed">Not Completed</option>
                        <option value="completed">Completed</option>
                      </select>
                    </div>
                  </div>

                  <div className="mt-auto d-flex gap-2">
                    <button className="btn btn-sm btn-outline-light w-50" onClick={() => handleEdit(todo)}>
                      Edit
                    </button>
                    <button className="btn btn-sm btn-danger w-50" onClick={() => handleDelete(todo.id)}>
                      Delete
                    </button>
                  </div>
                </div>
              </article>
            </div>
          ))}
        </div>

        <footer className="text-center text-muted mt-5 small">
          Built with ‚ù§Ô∏è ‚Äî colorful, animated, and ready to use
        </footer>
      </div>
    </div>
  );
}
