import React, { useEffect, useState } from "react";
import TodoForm from "./components/TodoForm";
import TodoCard from "./components/TodoCard";
import Filter from "./components/Filter";
import ThemeToggle from "./components/ThemeToggle";

const LOCAL_KEY = "react_todo_tasks_v1";
const THEME_KEY = "react_todo_theme_v1";

export default function App() {
  const [todos, setTodos] = useState([]);
  const [editingTodo, setEditingTodo] = useState(null);
  const [filter, setFilter] = useState("all"); // all | completed | notcompleted
  const [theme, setTheme] = useState("romantic"); // default: romantic bright

  // Load todos from localStorage on mount
  useEffect(() => {
    const raw = localStorage.getItem(LOCAL_KEY);
    if (raw) {
      try {
        setTodos(JSON.parse(raw));
      } catch {
        setTodos([]);
      }
    }
    const savedTheme = localStorage.getItem(THEME_KEY);
    if (savedTheme) setTheme(savedTheme);
  }, []);

  // Save todos to localStorage
  useEffect(() => {
    localStorage.setItem(LOCAL_KEY, JSON.stringify(todos));
  }, [todos]);

  // apply theme to document root & persist
  useEffect(() => {
    document.documentElement.setAttribute("data-theme", theme);
    localStorage.setItem(THEME_KEY, theme);
  }, [theme]);

  function addTodo(data) {
    const newTodo = {
      id: Date.now().toString(),
      name: data.name,
      desc: data.desc,
      status: data.status || "notcompleted", // default
      createdAt: new Date().toISOString(),
    };
    setTodos(prev => [newTodo, ...prev]);
  }

  function updateTodo(id, updates) {
    setTodos(prev => prev.map(t => (t.id === id ? { ...t, ...updates } : t)));
    setEditingTodo(null);
  }

  function removeTodo(id) {
    if (!window.confirm("Delete this task?")) return;
    setTodos(prev => prev.filter(t => t.id !== id));
  }

  function changeStatus(id, newStatus) {
    setTodos(prev => prev.map(t => (t.id === id ? { ...t, status: newStatus } : t)));
  }

  const filtered = todos.filter((t) => {
    if (filter === "all") return true;
    if (filter === "completed") return t.status === "completed";
    if (filter === "notcompleted") return t.status === "notcompleted";
    return true;
  });

  return (
    <div className="app-root">
      <header className="app-header">
        <div className="brand">
          <div className="logo-blob" aria-hidden />
          <h1>Romantic Todo ✨</h1>
        </div>

        <div className="header-row">
          <p className="sub">Create tasks — romantic palette, animated UI & full CRUD</p>
          <ThemeToggle value={theme} onChange={setTheme} />
        </div>
      </header>

      <main className="container">
        <aside className="left">
          <TodoForm
            key={editingTodo ? editingTodo.id : "new"}
            onAdd={addTodo}
            onUpdate={updateTodo}
            editing={editingTodo}
            onCancel={() => setEditingTodo(null)}
          />
          <Filter
            value={filter}
            onChange={setFilter}
            counts={{
              all: todos.length,
              completed: todos.filter(t => t.status === "completed").length,
              notcompleted: todos.filter(t => t.status === "notcompleted").length
            }}
          />
        </aside>

        <section className="right">
          <div className="cards-grid">
            {filtered.length === 0 ? (
              <div className="empty-state">
                <div className="sparkle" />
                <p>No tasks yet — add your first romantic todo.</p>
                <small>Use the form on the left to create one.</small>
              </div>
            ) : (
              filtered.map((todo, i) => (
                <TodoCard
                  key={todo.id}
                  todo={todo}
                  onEdit={() => setEditingTodo(todo)}
                  onDelete={() => removeTodo(todo.id)}
                  onChangeStatus={(s) => changeStatus(todo.id, s)}
                  index={i}
                />
              ))
            )}
          </div>
        </section>
      </main>

      <footer className="footer">
        <small>Made with ❤️ — switch theme anytime</small>
      </footer>
    </div>
  );
}
