import React, { useState, useEffect } from "react";

export default function App() {
  const [todos, setTodos] = useState(() => {
    const saved = localStorage.getItem("todos");
    return saved ? JSON.parse(saved) : [];
  });

  const [taskName, setTaskName] = useState("");
  const [taskDesc, setTaskDesc] = useState("");
  const [filter, setFilter] = useState("all");
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (!taskName.trim()) return;

    if (editId) {
      setTodos((prev) =>
        prev.map((todo) =>
          todo.id === editId
            ? { ...todo, name: taskName, description: taskDesc }
            : todo
        )
      );
      setEditId(null);
    } else {
      setTodos([
        ...todos,
        {
          id: Date.now(),
          name: taskName,
          description: taskDesc,
          status: "not_completed",
        },
      ]);
    }

    setTaskName("");
    setTaskDesc("");
  };

  const handleDelete = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const handleEdit = (todo) => {
    setTaskName(todo.name);
    setTaskDesc(todo.description);
    setEditId(todo.id);
  };

  const handleStatusChange = (id, status) => {
    setTodos((prev) =>
      prev.map((todo) =>
        todo.id === id ? { ...todo, status: status } : todo
      )
    );
  };

  const filteredTodos = todos.filter((todo) => {
    if (filter === "completed") return todo.status === "completed";
    if (filter === "not_completed") return todo.status === "not_completed";
    return true;
  });

  return (
    <div style={{ maxWidth: "800px", margin: "20px auto", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center", color: "teal" }}>My Todo</h1>

      {/* Input Form */}
      <form onSubmit={handleAddOrUpdate} style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Todo Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          style={{ flex: 1, padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
        />
        <input
          type="text"
          placeholder="Todo Description"
          value={taskDesc}
          onChange={(e) => setTaskDesc(e.target.value)}
          style={{ flex: 2, padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
        />
        <button
          type="submit"
          style={{ padding: "8px 16px", background: "teal", color: "white", border: "none", borderRadius: "5px" }}
        >
          {editId ? "Update" : "Add"}
        </button>
      </form>

      {/* Filter */}
      <div style={{ marginBottom: "15px", textAlign: "right" }}>
        <label>Status: </label>
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          style={{ padding: "5px", borderRadius: "5px" }}
        >
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="not_completed">Not Completed</option>
        </select>
      </div>

      {/* Todo Cards */}
      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "15px" }}>
        {filteredTodos.length === 0 ? (
          <p>No todos found</p>
        ) : (
          filteredTodos.map((todo) => (
            <div
              key={todo.id}
              style={{
                background: "#e6ffed",
                padding: "15px",
                borderRadius: "8px",
                border: "1px solid #b2f5ea",
                boxShadow: "0 2px 5px rgba(0,0,0,0.05)",
              }}
            >
              <h3 style={{ margin: "0 0 10px" }}>Name: {todo.name}</h3>
              <p style={{ margin: "0 0 10px", fontSize: "14px" }}>Description: {todo.description || "-"}</p>
              <div>
                <label>Status: </label>
                <select
                  value={todo.status}
                  onChange={(e) => handleStatusChange(todo.id, e.target.value)}
                  style={{ padding: "5px", borderRadius: "5px" }}
                >
                  <option value="not_completed">Not Completed</option>
                  <option value="completed">Completed</option>
                </select>
              </div>
              <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
                <button
                  onClick={() => handleEdit(todo)}
                  style={{ flex: 1, padding: "6px", background: "#007bff", color: "white", border: "none", borderRadius: "5px" }}
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(todo.id)}
                  style={{ flex: 1, padding: "6px", background: "#dc3545", color: "white", border: "none", borderRadius: "5px" }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
