import React, { useEffect, useState } from "react";
import "./App.css";

export default function App() {
  const [todos, setTodos] = useState(() => {
    try {
      const raw = localStorage.getItem("todos_v3");
      return raw ? JSON.parse(raw) : [];
    } catch {
      return [];
    }
  });

  const [taskName, setTaskName] = useState("");
  const [taskDesc, setTaskDesc] = useState("");
  const [filter, setFilter] = useState("all");
  const [editId, setEditId] = useState(null);
  const [theme, setTheme] = useState("dark");

  useEffect(() => {
    localStorage.setItem("todos_v3", JSON.stringify(todos));
  }, [todos]);

  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  const resetForm = () => {
    setTaskName("");
    setTaskDesc("");
    setEditId(null);
  };

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (!taskName.trim()) return;

    if (editId) {
      setTodos((prev) =>
        prev.map((t) =>
          t.id === editId ? { ...t, name: taskName, description: taskDesc } : t
        )
      );
      resetForm();
    } else {
      const newTodo = {
        id: Date.now(),
        name: taskName.trim(),
        description: taskDesc.trim(),
        status: "not_completed",
        createdAt: new Date().toISOString(),
      };
      setTodos((prev) => [newTodo, ...prev]);
      resetForm();
    }
  };

  const handleDelete = (id) => {
    if (!window.confirm("Delete this task?")) return;
    setTodos((prev) => prev.filter((t) => t.id !== id));
  };

  const handleEdit = (todo) => {
    setTaskName(todo.name);
    setTaskDesc(todo.description);
    setEditId(todo.id);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleStatusChange = (id, status) => {
    setTodos((prev) => prev.map((t) => (t.id === id ? { ...t, status } : t)));
  };

  const filteredTodos = todos.filter((t) => {
    if (filter === "completed") return t.status === "completed";
    if (filter === "not_completed") return t.status === "not_completed";
    return true;
  });

  return (
    <div className="page-bg min-vh-100 py-5">
      <div className="container">
        <header className="d-flex justify-content-between align-items-center mb-4">
          <div>
            <h1 className="display-6 gradient-text animate-pop">üåü Todo App</h1>
            <p className="lead text-muted">Light & Dark Mode Ready</p>
          </div>
          <button
            className="btn btn-theme-toggle"
            onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
          >
            {theme === "dark" ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
          </button>
        </header>

        {/* Form */}
        <div className="card glass p-3 mb-4 shadow-lg blur-in">
          <form className="row g-2" onSubmit={handleAddOrUpdate}>
            <div className="col-md-4">
              <input
                className="form-control input-glow"
                placeholder="Task Name"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
                required
              />
            </div>
            <div className="col-md-5">
              <input
                className="form-control input-glow"
                placeholder="Description"
                value={taskDesc}
                onChange={(e) => setTaskDesc(e.target.value)}
              />
            </div>
            <div className="col-md-3 d-grid">
              <button type="submit" className="btn btn-hero">
                {editId ? "Update Task" : "Add Task"}
              </button>
            </div>
          </form>
        </div>

        {/* Filter */}
        <div className="d-flex justify-content-between align-items-center mb-3">
          <select
            className="form-select w-auto"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          >
            <option value="all">All</option>
            <option value="completed">Completed</option>
            <option value="not_completed">Not Completed</option>
          </select>
          <button
            className="btn btn-outline-light btn-sm"
            onClick={() => {
              if (!window.confirm("Clear all tasks?")) return;
              setTodos([]);
            }}
          >
            Clear All
          </button>
        </div>

        {/* Todo list */}
        <div className="row g-3">
          {filteredTodos.map((todo) => (
            <div key={todo.id} className="col-md-4">
              <div className="card todo-card shadow-sm tilt-in">
                <div className="card-body">
                  <h5 className="card-title">{todo.name}</h5>
                  <p className="card-text">{todo.description}</p>
                  <div className="d-flex align-items-center mb-2">
                    <span
                      className={`status-badge ${
                        todo.status === "completed" ? "done" : "pending"
                      }`}
                    >
                      {todo.status === "completed"
                        ? "Completed"
                        : "Not Completed"}
                    </span>
                    <select
                      className="form-select form-select-sm ms-2"
                      value={todo.status}
                      onChange={(e) =>
                        handleStatusChange(todo.id, e.target.value)
                      }
                    >
                      <option value="not_completed">Not Completed</option>
                      <option value="completed">Completed</option>
                    </select>
                  </div>
                  <div className="d-flex gap-2">
                    <button
                      className="btn btn-outline-light btn-sm"
                      onClick={() => handleEdit(todo)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => handleDelete(todo.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}

          {filteredTodos.length === 0 && (
            <p className="text-center text-muted">No tasks found</p>
          )}
        </div>
      </div>
    </div>
  );
}
